import React from 'react'
import { FcInfo } from "react-icons/fc";
import { RiDeleteBin5Line } from "react-icons/ri";
import { FaBan } from "react-icons/fa";
import './Offers.css'
import { GrAdd } from "react-icons/gr";
import { IoIosNotificationsOutline } from "react-icons/io";



class Offers extends React.Component {
  constructor() {
    super();
    this.state = {
      restaurants: [
        {
          id: "1",
          name: "Reef Demeshqy",
          img: "https://scontent.fcai21-4.fna.fbcdn.net/v/t1.6435-9/52596226_325651728083488_3388384662857449472_n.jpg?_nc_cat=106&ccb=1-3&_nc_sid=174925&_nc_ohc=qkcTMReM4CoAX-LxDPZ&_nc_ht=scontent.fcai21-4.fna&oh=84e0afb50c9b8d09efb48e59cd3192a3&oe=60D1E699",
          offers: [
            {
              name: "Reef Demeshqy 2 persons offer",
              includedItems: [
                {
                  name: "Reef Demeshqy combolarge",
                  price: "80 L.E",
                  quantity: "1",
                  img: "https://scontent.fcai21-3.fna.fbcdn.net/v/t1.6435-9/191389650_3951087934944693_4353788920767682985_n.jpg?_nc_cat=100&ccb=1-3&_nc_sid=e3f864&_nc_ohc=UFrgMCCB4LkAX8_4lJ1&_nc_ht=scontent.fcai21-3.fna&oh=45a1b5c7cc61e67549371542b54d9b89&oe=60D2A0F3",
                },
              ],
              offerPrice: "100",
            },
          ],
        },
        {
          id: "2",
          name: "Mcdonalds",
          img: "https://scontent.fcai21-3.fna.fbcdn.net/v/t1.6435-9/191389650_3951087934944693_4353788920767682985_n.jpg?_nc_cat=100&ccb=1-3&_nc_sid=e3f864&_nc_ohc=UFrgMCCB4LkAX8_4lJ1&_nc_ht=scontent.fcai21-3.fna&oh=45a1b5c7cc61e67549371542b54d9b89&oe=60D2A0F3",
          offers: [
            {
              name: "Mcdonalds persons offer",
              includedItems: [
                {
                  name: "Mcdonalds combolarge",
                  price: "80 L.E",
                  quantity: "1",
                  img: "data:image/webp;base64,",
                },
              ],
              offerPrice: "150",
            },
          ],
        },
      ],
    };
  }
  render() {
    return (
      <div>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: " center",
            color: "rgb(33, 33, 33)",
            backgroundColor: "rgb(246, 246, 246)",
            marginTop: "5px",
            marginBottom: "15px",
            paddingInline: "20px",
            fontSize: "18px",
          }}
        >
          <div>
            <div
              className="input-group rounded"
              style={{ width: 600, marginTop: "15px", float: "right" }}
            >
              <input
                type="search"
                className="form-control rounded"
                placeholder="Search for a restaurant"
                aria-label="Search"
                aria-describedby="search-addon"
              />

              <span className="input-group-text border-0" id="search-addon">
                <i className="fas fa-search"></i>
              </span>
            </div>
          </div>
        </div>

        <div className="row">
          {this.state.restaurants.length > 0
            ? this.state.restaurants.map((restaurant) => {
                return (
                  <div
                    className="card "
                    style={{
                      width: "260px",
                      marginLeft: "16px",
                      marginRight: "5px",
                      marginTop: "20px",
                      marginBottom: "8px",
                      display:"flex",
                      flexDirection:"coloumn" ,
                      justifyContent:"center",

                    }}
                  >
                    <img
                      className="card-img-top"
                      src={restaurant.img}
                      style={{ paddingLeft: "0px", paddingRight: "0px",width:"235px" ,height:"170px"}}
                      alt="Card image cap"

                    ></img>
                    <div
                      className="card-body text-center"
                      style={{ paddingBottom: "0px" }}
                    >
                      <h5 className="card-title text-center">
                        {restaurant.name}
                      </h5>

                      <ul className="list-group list-group-flush">
                        <li className="list-group-item">
                          <div className="text-center">
                            {/*                          
                            <button type="button" class="btn" data-toggle="modal" data-target={`#${restaurant.id}`}><FcInfo />
                              Copouns</button> */}
                            <button
                              type="button"
                              class="btn "
                              data-toggle="modal"
                              data-target={`#${restaurant.id}`}
                            >
                              <FcInfo />
                              Offers
                            </button>

                            {/* <div class="modal fade "  id={restaurant.id} tabindex="-1" role="dialog" aria-labelledby="myLargeModalLabel" aria-hidden="true">
                            <div class="modal-dialog modal-lg"> */}

                            <div
                              class="modal fade"
                              id={restaurant.id}
                              tabindex="-1"
                              role="dialog"
                              aria-labelledby="exampleModalLongTitle"
                              aria-hidden="true"
                            >
                              <div class="modal-dialog" role="document">
                                <div class="modal-content">
                                  <div class="modal-header">
                                    <h5
                                      class="modal-title"
                                      id="exampleModalLongTitle"
                                    >
                                      {restaurant.name} Offers
                                    </h5>
                                    <button
                                      type="button"
                                      class="close"
                                      data-dismiss="modal"
                                      aria-label="Close"
                                    >
                                      <span aria-hidden="true">&times;</span>
                                    </button>
                                  </div>
                                  <div class="modal-body">
                                    <ViewOffers
                                      resOffers={restaurant.offers}
                                    />
                                  </div>
                                  <div class="modal-footer">
                                    <button
                                      type="button"
                                      class="btn btn-secondary"
                                      data-dismiss="modal"
                                    >
                                      Close
                                    </button>
                                    <button
                                      type="button"
                                      class="btn btn-primary"
                                    >
                                      Save changes
                                    </button>
                                  </div>
                                </div>
                              </div>
                            </div>
                          </div>
                        </li>
                        <li className="list-group-item">
                          <a href="/newoffer" className="card-link">
                            <GrAdd />
                            Add new offer
                          </a>
                        </li>
                      </ul>
                    </div>
                  </div>
                );
              })
            : "There's not any Offers yet"}
        </div>
      </div>
    );
  }
}

class ViewOffers extends React.Component {
  render() {
    return (
      <div>
        {this.props.resOffers.length > 0
          ? this.props.resOffers.map((singleOffer) => {
              return (
                <div>
                  <h1
                    style={{
                      color: "rgb(33, 33, 33)",
                      backgroundColor: "rgb(246, 246, 246)",
                      marginBottom: "10px",
                      paddingInline: "20px",
                      paddingTop: "10px",
                      paddingBottom: "10px",
                      fontSize: "15px",
                      fontFamily: "sans-serif",
                    }}
                  >
                    <div className="row">
                      <div className="col-7">
                        {" "}
                        <b> {singleOffer.name} </b>{" "}
                      </div>

                      <div className="col-4">
                        {" "}
                        <b> price : </b> {singleOffer.offerPrice} L.E
                      </div>
                    </div>
                  </h1>
                  {singleOffer.includedItems.length > 0
                    ? singleOffer.includedItems.map((item) => {
                        return (
                          <div>
                            <div className="card" style={{ width: "450px" }}>
                              <div className="card-body">
                                <div
                                  className="list-group list-group-flush"
                                  style={{
                                    display: "flex",
                                  }}
                                >
                                  <div style={{ marginLeft: "0px" }}>
                                    <img
                                      src={item.img}
                                      style={{ width: "100px", height: "100px" }}
                                    />
                                    <b> [{item.quantity}] </b>
                                    {item.name}
                                    <div style={{ marginLeft: "10px" }}>
                                      {" "}
                                      <b> {item.price} </b>{" "}
                                    </div>
                                  </div>
                                </div>
                              </div>
                            </div>
                          </div>
                        );
                      })
                    : "No offer items"}
                </div>
              );
            })
          : "Empty"}
      </div>
    );
  }
}
