import React from 'react'
import { FcInfo } from "react-icons/fc";
import { RiDeleteBin5Line } from "react-icons/ri";
import { FaBan } from "react-icons/fa";
import './Offers.css'
import { GrAdd } from "react-icons/gr";
import { IoIosNotificationsOutline } from "react-icons/io";



class Offers extends React.Component {
  constructor() {
    super();
    this.state = {
      restaurants: [
        {
          RestaurantName: "reef demeshqy",
          offers: [
            {
              name: "Pick 'N Go 2 persons offer",
              includedItems: [
                {
                  name: "pick n go combolarge",
                  price: "80 L.E",
                  quantity: "1",
                  img: "data:image/webp;base64,",
                },
              ],
              offerPrice: "100",
            },
          ],
        },
        {
          RestaurantName: "mcDonals",
          offers: [
            {
              name: "Pick 'N Go 2 persons offer",
              includedItems: [
                {
                  name: "pick n go combolarge",
                  price: "80 L.E",
                  quantity: "1",
                  img: "data:image/webp;base64,",
                },
              ],
              offerPrice: "150",
            },
          ],
        },
      ],
    };
  }
  render() {
    return (
      <div>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: " center",
            color: "rgb(33, 33, 33)",
            backgroundColor: "rgb(246, 246, 246)",
            marginTop: "5px",

            paddingInline: "20px",

            fontSize: "22px",
          }}
        >
          <div>
            <div
              className="input-group rounded"
              style={{ width: 600, marginTop: "15px", float: "right" }}
            >
              <input
                type="search"
                className="form-control rounded"
                placeholder="Search for a restaurant"
                aria-label="Search"
                aria-describedby="search-addon"
              />

              <span className="input-group-text border-0" id="search-addon">
                <i className="fas fa-search"></i>
              </span>
            </div>
          </div>
        </div>
        <br></br>
        <div className="row">
          <div className="col-lg-3">
            <div className="card">
              <img
                className="card-img-top"
                src="https://media-cdn.tripadvisor.com/media/photo-m/1280/17/75/3f/d1/restaurant-in-valkenswaard.jpg"
                alt="Card image cap"
              ></img>
              <div className="card-body">
                <h5 className="card-title">Cilantro</h5>
                <p className="card-text">
                  Some quick example text to build on the card title and make up
                  the bulk of the card's content.
                </p>
              </div>
              <ul className="list-group list-group-flush">
                <li className="list-group-item">
                  <a href="#" className="card-link">
                    <FcInfo />
                    View Offer
                  </a>
                </li>
                <li className="list-group-item">
                  <a href="/newoffer" className="card-link">
                    <GrAdd />
                    Add new offer
                  </a>
                </li>
                <li>
                  <button
                    type="button"
                    class="btn btn-primary"
                    data-toggle="modal"
                    data-target="#exampleModalLong"
                  >
                    Launch demo modal
                  </button>

                  <div
                    class="modal fade"
                    id="exampleModalLong"
                    tabindex="-1"
                    role="dialog"
                  >
                    <div class="modal-dialog" role="document">
                      <div class="modal-content">
                        <div class="modal-header">
                          <h5 class="modal-title" id="exampleModalLongTitle">
                            Modal title
                          </h5>
                          <button
                            type="button"
                            class="close"
                            data-dismiss="modal"
                            aria-label="Close"
                          >
                            <span aria-hidden="true">&times;</span>
                          </button>
                        </div>
                        <div class="modal-body">...</div>
                        <div class="modal-footer">
                          <button
                            type="button"
                            class="btn btn-secondary"
                            data-dismiss="modal"
                          >
                            Close
                          </button>
                          <button type="button" class="btn btn-primary">
                            Save changes
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                </li>
              </ul>
            </div>

            <div className="input-group rounded" style={{ width: 300 }}>
              <br></br>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
export default Offers
